// frontend/src/components/SoilFertilityPredict.js
import React, { useState } from 'react';
import axios from 'axios';

function SoilFertilityPredict() {
  const [features, setFeatures] = useState(Array(12).fill(''));
  const [prediction, setPrediction] = useState('');
  const [confidence, setConfidence] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (index, value) => {
    const newFeatures = [...features];
    newFeatures[index] = value;
    setFeatures(newFeatures);
  };

  const handlePredict = async () => {
    setLoading(true);
    setPrediction('');
    setConfidence(null);

    try {
      const res = await axios.post('http://localhost:5001/soil-predict', {
        features: features.map(f => parseFloat(f))
      });

      console.log(res.data);
      setPrediction(res.data.predicted_fertility);
      setConfidence(res.data.confidence);
    } catch (error) {
      console.error(error);
      setPrediction('Error predicting fertility.');
    }

    setLoading(false);
  };

  const labels = ['N', 'P', 'K', 'pH', 'EC', 'OC', 'S', 'Zn', 'Fe', 'Cu', 'Mn', 'B'];

  return (
    <div style={styles.card}>
      <h2 style={styles.title}>ðŸŒ± Soil Fertility Prediction</h2>
      {labels.map((label, idx) => (
        <div key={idx} style={styles.inputGroup}>
          <label>{label}:</label>
          <input
            type="number"
            value={features[idx]}
            onChange={(e) => handleChange(idx, e.target.value)}
            style={styles.input}
          />
        </div>
      ))}

      <button onClick={handlePredict} style={styles.button}>
        Predict Fertility
      </button>

      {loading && <p style={styles.loading}>ðŸ”„ Predicting...</p>}

      {!loading && prediction && (
        <div style={styles.result}>
          <h3 style={styles.resultHeader}>ðŸ§ª Result</h3>
          <p><strong>Fertility:</strong> {prediction}</p>
          {confidence !== null && (
            <p><strong>Confidence:</strong> {confidence.toFixed(2)}%</p>
          )}
        </div>
      )}
    </div>
  );
}

const styles = {
  card: {
    margin: '0 auto',
    padding: '30px',
    maxWidth: '600px',
    backgroundColor: '#ffffff',
    borderRadius: '12px',
    boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
    textAlign: 'center',
  },
  title: {
    marginBottom: '20px',
    color: '#388E3C',
  },
  inputGroup: {
    marginBottom: '10px',
    display: 'flex',
    justifyContent: 'space-between',
    padding: '0 20px',
  },
  input: {
    width: '100px',
    padding: '5px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#4CAF50',
    color: '#fff',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    marginTop: '20px',
  },
  loading: {
    marginTop: '20px',
    color: '#888',
    fontStyle: 'italic',
  },
  result: {
    marginTop: '30px',
    backgroundColor: '#f1f8e9',
    padding: '15px',
    borderRadius: '8px',
  },
  resultHeader: {
    marginBottom: '10px',
    color: '#2E7D32',
  },
};

export default SoilFertilityPredict;
