import WeatherComponent from './components/Weather';
import CropRecommendation from './components/CropRecommendation';

function App() {
  return (
    <div className="App" style={styles.container}>
      <h1 style={styles.header}>ðŸŒ¿ Plant Disease, Soil Fertility & Weather Detection App</h1>

      {/* Weather Component */}
      <section style={styles.section}>
        <h2 style={styles.subHeader}>ðŸŒ¦ Current Weather Details</h2>
        <WeatherComponent />
      </section>

      <hr style={styles.separator} />

      {/* Plant Disease Upload */}
      <section style={styles.section}>
        <h2 style={styles.subHeader}>ðŸ¦  Plant Disease Detection</h2>
        <PlantDiseaseUpload />
      </section>

      <hr style={styles.separator} />

      {/* Soil Fertility Prediction */}
      <section style={styles.section}>
        <h2 style={styles.subHeader}>ðŸŒ± Soil Fertility Prediction</h2>
        <SoilFertilityPredict />
      </section>

      <hr style={styles.separator} />

      {/* Soil Fertility Prediction */}
      <section style={styles.section}>
        <h2 style={styles.subHeader}>ðŸŒ± Crop Recommendation</h2>
        <CropRecommendation />
      </section>

      {/* Soil Fertility Prediction */}
      <section style={styles.section}>
        <h2 style={styles.subHeader}>ðŸŒ± PestDetection</h2>
        <PestDetectionUpload />
      </section>
    </div>
  );
}

const styles = {
  container: {
    textAlign: 'center',
    marginTop: '30px',
    fontFamily: 'Segoe UI, sans-serif',
    backgroundColor: '#f9f9f9',
    minHeight: '100vh',
    padding: '40px',
  },
  header: {
    color: '#2E7D32',
    marginBottom: '30px',
    fontSize: '2.8rem',
    letterSpacing: '1px',
  },
  subHeader: {
    fontSize: '1.8rem',
    color: '#1b5e20',
    marginBottom: '20px',
  },
  section: {
    marginBottom: '60px',
  },
  separator: {
    margin: '40px 0',
    borderColor: '#ccc',
  },
};

export default App;
