import React, { useState } from 'react';
import axios from 'axios';

function PestDetectionUpload() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    setLoading(true);
    setResult(null);

    try {
      const res = await axios.post('http://localhost:5004/detect-pest', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      setResult(res.data);
    } catch (err) {
      console.error("Error detecting pest:", err);
      setResult({ error: "Error detecting pest" });
    }

    setLoading(false);
  };

  return (
    <div style={styles.card}>
      <h2>Pest Detection</h2>
      <input
        type="file"
        onChange={(e) => setFile(e.target.files[0])}
      />
      <button onClick={handleUpload}>Detect Pest</button>

      {loading && <p>Analyzing image...</p>}

      {result && !result.error && (
        <div>
          <p><strong>Pest:</strong> {result.pest_detected}</p>
          <p><strong>Risk:</strong> {result.risk}</p>
          <p><strong>Confidence:</strong> {result.confidence.toFixed(2)}%</p>
        </div>
      )}

      {result && result.error && (
        <p>Error: {result.error}</p>
      )}
    </div>
  );
}

const styles = {
  card: {
    padding: '20px',
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    textAlign: 'center',
  }
};

export default PestDetectionUpload;
