import React, { useState } from 'react'; import axios from 'axios';

function PlantDiseaseUpload() { const [file, setFile] = useState(null); const [prediction, setPrediction] = useState(""); const [loading, setLoading] = useState(false);

const handleUpload = async () => { if (!file) return;

const formData = new FormData();
formData.append('file', file);

setLoading(true);
try {
  const res = await axios.post('http://localhost:5000/predict', formData, {
    headers: { 'Content-Type': 'multipart/form-data' }
  });
  console.log("Backend response:", res.data); // âœ… Debug output
  setPrediction(res.data.prediction);
} catch (err) {
  console.error("Prediction error:", err);
  setPrediction("Error predicting disease");
}
setLoading(false);
};

return ( <div className="upload-form"> <h2>ðŸŒ¿ Plant Disease Detection</h2> <input type="file" onChange={(e) => setFile(e.target.files[0])} /> <button onClick={handleUpload}>Upload & Predict</button>

  {loading && <p>Loading...</p>}

  {!loading && prediction && (
    <div style={{ marginTop: '20px' }}>
      <h3>Prediction:</h3>
      <pre style={{ background: '#f4f4f4', padding: '10px', borderRadius: '5px' }}>
        {JSON.stringify(prediction, null, 2)}
      </pre>
    </div>
  )}
</div>
); }

export default PlantDiseaseUpload;
