import React, { useState } from 'react';
import axios from 'axios';

function WeatherComponent() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState('');

  const fetchWeather = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/weather?city=${city}`);
      setWeather(res.data);
      setError('');
    } catch (err) {
      console.error('Error fetching weather:', err.message);
      setError('Could not fetch weather. Please try again.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>ðŸŒ¦ Get Current Weather</h2>
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city name"
      />
      <button onClick={fetchWeather} style={{ marginLeft: '10px' }}>
        Get Weather
      </button>

      {weather && (
        <div style={{ marginTop: '20px' }}>
          <p><strong>Temperature:</strong> {weather.temperature}Â°C</p>
          <p><strong>Humidity:</strong> {weather.humidity}%</p>
          <p><strong>Rain (last 1h):</strong> {weather.rain} mm</p>
          <p><strong>Condition:</strong> {weather.weather}</p>
        </div>
      )}

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default WeatherComponent;
