import React, { useState, useEffect } from 'react';
import axios from 'axios';

function WeatherComponent() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [alert, setAlert] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchWeather = async () => {
    if (!city) return;

    setLoading(true);
    setAlert('');
    try {
      const response = await axios.get(`http://localhost:5003/api/weather?city=${city}`);
      setWeather(response.data);
      checkWeatherAlert(response.data); // ‚¨ÖÔ∏è Check for weather warnings
    } catch (error) {
      console.error('Error fetching weather:', error);
    }
    setLoading(false);
  };

  const checkWeatherAlert = (data) => {
    if (!data || !data.weather || data.weather.length === 0) return;

    const condition = data.weather[0].main.toLowerCase();
    const temp = data.main.temp;
    const alerts = [];

    if (condition.includes('rain')) {
      alerts.push('‚ö†Ô∏è Rain expected! Prepare for harvesting.');
    }
    if (condition.includes('snow') || temp < 3) {
      alerts.push('‚ùÑÔ∏è Frost alert! Protect your crops.');
    }
    if (temp > 35) {
      alerts.push('‚òÄÔ∏è Heatwave warning! Irrigate sufficiently.');
    }

    setAlert(alerts.join(' '));
  };

  // Optional: Auto-refresh weather every X minutes
  useEffect(() => {
    const interval = setInterval(() => {
      if (city) {
        fetchWeather();
      }
    }, 10 * 60 * 1000); // every 10 minutes

    return () => clearInterval(interval);
  }, [city]);

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>üå¶ Weather Info & Alerts</h2>
      <input
        type="text"
        placeholder="Enter City"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={styles.input}
      />
      <button onClick={fetchWeather} style={styles.button}>
        {loading ? 'Loading...' : 'Get Weather'}
      </button>

      {alert && (
        <div style={styles.alert}>
          {alert}
        </div>
      )}

      {weather && (
        <div style={styles.card}>
          <h3>{weather.name}, {weather.sys.country}</h3>
          <p><strong>Temperature:</strong> {weather.main.temp} ¬∞C</p>
          <p><strong>Condition:</strong> {weather.weather[0].main} ({weather.weather[0].description})</p>
          <p><strong>Humidity:</strong> {weather.main.humidity}%</p>
          <p><strong>Wind Speed:</strong> {weather.wind.speed} m/s</p>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    margin: '40px auto',
    padding: '20px',
    maxWidth: '400px',
    backgroundColor: '#e3f2fd',
    borderRadius: '10px',
    textAlign: 'center',
  },
  title: {
    color: '#1565c0',
    marginBottom: '20px',
  },
  input: {
    padding: '10px',
    marginBottom: '10px',
    width: '80%',
    borderRadius: '5px',
    border: '1px solid #ccc',
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#1565c0',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginBottom: '20px',
  },
  alert: {
    backgroundColor: '#ffeb3b',
    color: '#c62828',
    padding: '10px',
    borderRadius: '8px',
    marginBottom: '20px',
    fontWeight: 'bold',
  },
  card: {
    backgroundColor: '#ffffff',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
    marginTop: '20px',
  },
};

export default WeatherComponent;
